import React from "react";
import { AgGridReact } from 'ag-grid-react';
import 'ag-grid-community/dist/styles/ag-grid.css';
import 'ag-grid-community/dist/styles/ag-theme-alpine.css';

const GroupTable = ({ height, n, columnDefs }) => {
  // Generate row data with 'n' number of rows
  const rowData = [];
  for (let i = 1; i <= n; i++) {
    rowData.push({ id: i, name: `Row ${i}`, age: 20 + i, component: `Component ${i}` });
  }

  // Modify the first column to be of type 'text'
   const modifiedColumnDefs = columnDefs.map((col, index) => {
    if (index === 0) {
      return { ...col, type: 'text', editable: true };
    }
    return col;
  });

  return (
    <div className="ag-theme-alpine" style={{ height: height, width: '100%', position: 'relative' }}>
      <AgGridReact
        columnDefs={modifiedColumnDefs}
        rowData={rowData}
        defaultColDef={{
          flex: 1,
          resizable: true,
          filter: true,
          sortable: true,
          editable: true,
        }}
      ></AgGridReact>
    </div>
  );
};

export default GroupTable;
