import React, { useState, useEffect, useRef } from "react";
// import styles from "./TaskDashboard.module.css";
import {  InputLabel, TextField,makeStyles,Button, Switch, Input} from "@material-ui/core";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip,Label,ReferenceLine } from 'recharts';
// import { arr,arr2 } from "./data";
import {
  MuiPickersUtilsProvider,
  KeyboardDatePicker,
} from "@material-ui/pickers";
import { AgGridColumn } from "ag-grid-react";
import MomentUtils from "@date-io/moment";
import Navigation from "../../components/navigation/Navigation";
import styles from "./tDashboard.module.css";
import moment from "moment";
import CustomSelect from "../../ui/Form/CustomSelect";
import ReliabilityChart from "../Reliability Dashboard/ReliabilityChart";
import MissionSlider from "../Reliability Dashboard/MissionSlider";
import AddCircleOutlineIcon from "@material-ui/icons/AddCircleOutline";
import { Autocomplete } from "@material-ui/lab";
import Loader from "react-loader-spinner";
import EventCalendar from "../Reliability Dashboard/EventCalendar";
import { data, subSystemLevelData, events, mainData } from "../Reliability Dashboard/DashboardData";
import ReactCardFlip from "react-card-flip";
import Table from "../../ui/Table/DataManagerTable";
import AddIcon from "@material-ui/icons/Add";
import { v4 as uuidv4 } from "uuid";
import DeleteIcon from "@material-ui/icons/Delete";

const MissionData = (props) => {
  
  return (
    <div className={styles.missionData} style={props.style}>
      <div className={styles.target}>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          class="icon icon-tabler icon-tabler-target"
          width="14"
          height="14"
          viewBox="0 0 24 24"
          stroke-width="1.5"
          stroke="#374c93"
          fill="none"
          stroke-linecap="round"
          stroke-linejoin="round"
        >
          <path stroke="none" d="M0 0h24v24H0z" fill="none" />
          <circle cx="12" cy="12" r="1" />
          <circle cx="12" cy="12" r="5" />
          <circle cx="12" cy="12" r="9" />
        </svg>{" "}
        Target: {props.mission.target}%
      </div>
      <div className={styles.actual}>
        <svg
          xmlns="http://www.w3.org/2000/svg"
          class="icon icon-tabler icon-tabler-shield-check"
          width="14"
          height="14"
          viewBox="0 0 24 24"
          stroke-width="1.5"
          stroke="#f8f8f8"
          fill="none"
          stroke-linecap="round"
          stroke-linejoin="round"
        >
          <path stroke="none" d="M0 0h24v24H0z" fill="none" />
          <path d="M9 12l2 2l4 -4" />
          <path d="M12 3a12 12 0 0 0 8.5 3a12 12 0 0 1 -8.5 15a12 12 0 0 1 -8.5 -15a12 12 0 0 0 8.5 -3" />
        </svg>
        Actual:
        {props.mission.actual.map((data) => {
          return (
            <>
              <div>
                {data.name} {data.rel.toFixed(2)}%
              </div>
            </>
          );
        })}
      </div>
      <div style={{ textAlign: "center" }}>
        <div className={styles.probabilityHead}>Probability of Achieving</div>
        <div className={styles.probability}>
          {props.mission.actual.map((data) => {
            return (
              <>
                <div>
                  {data.name}{" "}
                  {data.prob.toFixed(2) == 100
                    ? ">99%"
                    : `${data.prob.toFixed(2)}%`}
                </div>
              </>
            );
          })}
        </div>
      </div>
    </div>
  );
};


const TaskDashboard = () => {
  const [gridApi, setGridApi] = useState(null);
  const [missionProfileData, setMissionData] = useState([]);
  const [rowState, setRows] = useState([]);
  const ImportColumns = [
    <AgGridColumn
      field="missionType"
      headerName="Mission Type"
      headerTooltip="Mission Type"
      cellEditor="agSelectCellEditor"
      checkboxSelection={true}
      cellEditorParams={{
        values: [
          "",
          "Harbour",
          "Entry Leaving Harbour",
          "Cruise",
          "Defense Station",
          "Action Station",
        ],
      }}
      width="220"
      editable={true}
    />,
    <AgGridColumn
    field="duration"
    headerName="Duration"
    headerTooltip="Duration"
    type="number"
    width={100}
    editable={true}
  />
  ];
  const [shipOption, setshipOption] = useState([]);
  const [taskOption, settaskOption] = useState([]);
  const [missionOption, setmissionOption] = useState([]);
  const [loading, setLoading] = useState(false);

  const [selectedTask, setSelectedTask] = useState("");
  const [selectedMission, setSelectedMission] = useState("");

  // Reliability Dashboard
  const [showSubsystem, setSubSystem] = useState(false);
  const [startDate, setStartDate] = useState(new Date());

  const [isCardFlipped, setCardFlipped] = useState(false);

  const [currentMission, setMission] = useState(0);
  const [eqDataOption, setEqDataOption] = useState([]);

  const [selectedEqName, setEquipmentName] = useState(null);
  const [selectedShipName, setShipName] = useState(null);
  const [selectedMissionName, setMissionName] = useState(null);

  const [graphData, setGraphData] = useState([]);

  const [missionPhaseGraphData, setMissionPhaseGraphData] = useState([]);

  const [subSystemData, setSubSystemData] = useState([]);
  const [eventInfo, setEventInfo] = useState(null);
  const [cardData, setCardData] = useState(null);
  const missionName = useRef()
  const thReliab = useRef()
  const handleStartDateChange = (date) => {
    setStartDate(date);
  };
  const [endDate, setEndDate] = useState(new Date());
  const handleEndDateChange = (date) => {
    setEndDate(date);
  };

  const AddRow = () => {
    const defaultRow = [
      {
        id: uuidv4(),
        duration: 0,
        missionType: ""
      },
    ];
    gridApi.applyTransaction({
      add: defaultRow,
    });
  };
  const deleteRows = () => {
    debugger;
    const selectedRows = gridApi.getSelectedRows();
    gridApi.applyTransaction({ remove: selectedRows });
    let allRowData = [];
    gridApi.forEachNode((node) => allRowData.push(node.data));
    setMissionData(allRowData)
    // console.log(selectedRows);
  };

  const handleEventCheck = () => {
    let start = moment(startDate).subtract(1, "day");
    let end = moment(endDate).add(1, "day");
    let total = (end - start) / (1000 * 3600 * 24) - 1;
    console.log(total);
    let days = { working: 0, down: 0, maintenance: 0, total: total };
    events.forEach((event) => {
      if (event.start < end && event.end > start) {
        if (event.start >= start && event.end <= end) {
          let diff =
            (moment(event.end) - moment(event.start)) / (1000 * 3600 * 24);
          console.log(diff);
          days[event.status] += Math.floor(diff);
        } else if (event.start >= start && event.end >= end) {
          let diff = (moment(end) - moment(event.start)) / (1000 * 3600 * 24);
          console.log(diff);

          days[event.status] += Math.floor(diff);
        } else if (event.start <= start && event.end <= end) {
          let diff = (moment(event.end) - moment(start)) / (1000 * 3600 * 24);
          console.log(diff);

          days[event.status] += Math.floor(diff);
        } else if (event.start <= start && event.end >= end) {
          let diff = (moment(end) - moment(start)) / (1000 * 3600 * 24);
          console.log(diff);

          days[event.status] += Math.floor(diff);
        }
      }
    });
    setEventInfo(days);
    setCardFlipped(!isCardFlipped);
  };


  useEffect(() => {
    fetch("/task_dash_populate", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    })
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        debugger;
        const mission_data = data["missionData"];
        const taskNames = data["tasks"]
        setmissionOption(mission_data);
        settaskOption(taskNames);      
      });
  }, []);


  const dropDownStyle = makeStyles({
    root: {
      paddingLeft: 10,
      background: "#fff",
      border: "1px solid #0263a1",
      borderRadius: "5px",
      width: "320px",
      minHeight: "40px",
      boxShadow: "2px 3px 5px -1px rgba(0,0,0,0.2)",
    },
    inputRoot:{
      width: '100%'
    },
  });
  const classes=dropDownStyle();
  let arr=[]
  let arr2=[]
  for(let i=0;i<50;i++){
    // const maxDate = Date.now();
    // const timestamp = Math.floor(Math.random() * maxDate);
    arr=[...arr,{name:new Date(new Date(2021, 1, 1).getTime() + Math.random() * (new Date(2022, 1, 1).getTime() - new Date(2021, 1, 1).getTime())).toISOString().slice(0,10),uv:Math.floor(Math.random() * (80 - 30 + 1)) + 30}]

    arr2=[...arr2,{name:new Date(new Date(2021, 1, 1).getTime() + Math.random() * (new Date(2022, 1, 1).getTime() - new Date(2021, 1, 1).getTime())).toISOString().slice(0,10),uv:Math.floor(Math.random() * (75 - 30 + 1)) + 30}]
  }
  const [showGraph,setShowGraph]=useState(false);

  const onSubmitHandler = () => { 
    // setMission(0);
    if(missionProfileData.length > 0){
      const data = {"taskName": selectedTask, "selectedMission": missionName.current.value, "missionProfileData": missionProfileData}
      fetch("/task_rel", {
        method: "POST",
        body: JSON.stringify(data),
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
      })
        .then((res) => res.json())
        .then((d) => {
          debugger;
          let phase_data = d["phase_data"]
          d = d["main_data"]
          console.log(d);
          if(d.length > 0){
            let newGraphData = [];
            let newSubSystem = [];
            let newCardData = [];
            console.log(d);
            let mid = 0
            let missionData = d[mid][data["selectedMission"]];
            newGraphData = [
              ...newGraphData,
              {
                name: "Target Reliability",
                Reliability: 90,
              },
            ];
            newCardData[mid] = {
              ...newCardData[mid],
              name: data["selectedMission"],
              target: 90,
            };
              // missionOption.forEach((element) => {
              //   debugger;
              //   if (element.missionName === data["selectedMission"]) {
              //     newCardData[mid] = {
              //       ...newCardData[mid],
              //       name: data["selectedMission"],
              //       target: element.tar_rel,
              //     };
                  
              //   }
              // })//missionOption
              data.taskName.forEach((taskN) => {
                debugger;
                let shipData = missionData[taskN.name];
                //console.log(shipData);
                let actual = []; 
                actual[0] = {
                  name: taskN.name,
                  rel: Math.round(100 * shipData[0].rel, 2) ,
                  prob: Math.round(100 * shipData[0].prob_ac, 2),
                };
                newCardData[mid].actual = actual;
                newGraphData = [
                  ...newGraphData,
                  { name: `${taskN.name}`, Reliability: Math.round(100 * shipData[0].rel, 2) },
                ];
  
                let eqptData = shipData[0];
                eqptData.child.forEach((child, cid) => {
                    newSubSystem[cid] = {
                      ...newSubSystem[cid],
                      name: child.name,
                      [taskN.name]: Math.round(100 * child.rel, 2),
                    };
                  });
              });// More arrays populate
            // data.selectedMission.forEach((mission,mid) => {
            //   debugger;
            // })
            debugger;
            let phaseGraphData = []
            let keys = Object.keys(phase_data);
            keys.forEach((key, cid) => {
              phaseGraphData.push({name: key, Reliability: phase_data[key][1]})
            })
            setGraphData(newGraphData);
            setMissionPhaseGraphData(phaseGraphData);
                setSubSystemData(newSubSystem);
                setCardData(newCardData);
                setLoading(false);
  
          }
        });
    }else{
      alert("Hello")
    }
    
  };

  const updateFinalRowData = (d) => {
    setMissionData(d);
  };
  const minThreshold=45
  const maxThreshold=60
  return (
    <MuiPickersUtilsProvider utils={MomentUtils}>
      <Navigation />
      <div className={styles.body}>
        <div className={styles.mprofile}>
          {/* <div style={{ width: "300px" }}>
            <InputLabel
              style={{
                fontWeight: "bold",
                color: "black",
                fontSize: "16px",
                marginBottom: "10px",
              }}
            >
              Ship Name
            </InputLabel>
            <CustomSelect fields={['a','b','c','d','e']}/>
              
           
            
          </div> */}
          <div style={{ width: "300px" }}>
            <InputLabel
              style={{
                fontWeight: "bold",
                color: "black",
                fontSize: "16px",
                marginBottom: "10px",
              }}
            >
              Task Name
            </InputLabel>

            <Autocomplete
            classes={classes}
              multiple
              id="tags-standard"
              options={taskOption}
              getOptionLabel={(option) => option.name}
              onChange = {(event, value) => setSelectedTask(value)}
              renderInput={(params) => (
                <TextField
                  {...params}
                  InputProps={{...params.InputProps, disableUnderline: true}}
                  variant="standard"
                  // label="Multiple values"
                  // placeholder="Favorites"
                />
              )}
            />
            
          </div>
          
          {/* <div style={{ width: "300px" }}>
            <InputLabel
              style={{
                fontWeight: "bold",
                color: "black",
                fontSize: "16px",
                marginBottom: "10px",
              }}
            >
              Select Mission
            </InputLabel>

            <Autocomplete
            classes={classes}
              multiple
              id="tags-standard"
              options={missionOption}
              getOptionLabel={(option) => option.missionName}
              onChange={(event, value) => setSelectedMission(value)}
              renderInput={(params) => (
                <TextField
                  {...params}
                  InputProps={{...params.InputProps, disableUnderline: true}}
                  variant="standard"
                  // label="Multiple values"
                  // placeholder="Favorites"
                />
              )}
            />
            
          </div> */}

          <div style={{ width: "300px" }}>
            <InputLabel
              style={{
                fontWeight: "bold",
                color: "black",
                fontSize: "16px",
                marginBottom: "10px",
              }}
            >
               Mission Name
            </InputLabel>

            {/* <TextField ref={missionName}   id="outlined-basic" variant="outlined" /> */}
            <input
            className={styles.missionName}
                    ref={missionName}
                    // onChange={onHandleChange}
                  ></input>
            
          </div>

          <div style={{ width: "300px" }}>
            <InputLabel
              style={{
                fontWeight: "bold",
                color: "black",
                fontSize: "16px",
                marginBottom: "10px",
              }}
            >
               Threshold Reliability
            </InputLabel>

            {/* <TextField ref={missionName}   id="outlined-basic" variant="outlined" /> */}
            <input
            className={styles.missionName}
                    ref={thReliab}
                    // onChange={onHandleChange}
                  ></input>
            
          </div>

          <Button
            variant="contained"
            color="primary"
            style={{
              marginTop: "2rem",
            }}
            onClick={onSubmitHandler} 
          >{console.log(arr[arr.length-1]["uv"]+"hello2")}
            Submit
          </Button>
         
          
        </div>
        
        <div className={styles.table}>
        <Table
        columnDefs={ImportColumns}
        setGrid={setGridApi}
        gridApi={gridApi}
        rowData={rowState}
        tableUpdate={updateFinalRowData}
        tableSize= {290}
      />
      <div className={styles.tableFooter}>
            <Button
              variant="contained"
              startIcon={<AddIcon />}
              color="secondary"
              onClick={() => AddRow()}
            >
              Add Row
            </Button>
            <Button
              style={{ marginLeft: 10 }}
              variant="contained"
              startIcon={<DeleteIcon />}
              color="secondary"
              onClick={() => deleteRows()}
            >
              Delete Rows
            </Button>
          </div>
        {/* <div style={{ width: "300px" }}>
        <TextField id="outlined-basic" label="Mission Name" 
        variant="outlined" defaultValue="Temp Mission" />
          </div>
        <div style={{ width: "300px" }}>
        <TextField id="outlined-basic" label="Harbour Duration"
         variant="outlined" defaultValue="0" type="number"   />
          </div>
          <div style={{ width: "300px" }}>
          <TextField id="outlined-basic" label="Entry Leaving Harbour Duration"
           variant="outlined" defaultValue="0" type="number" />
          </div>
          <div style={{ width: "300px" }}>
          <TextField id="outlined-basic" label="Cruise Duration"
           variant="outlined" defaultValue="0" type="number" />
          </div>
          <div style={{ width: "300px" }}>
          <TextField id="outlined-basic" label="Defence Duration"
           variant="outlined" defaultValue="0" type="number" />
          </div>
          <div style={{ width: "300px" }}>
          <TextField id="outlined-basic" label="Action Duration" 
          variant="outlined" defaultValue="0" type="number" />
          </div> */}
        
        </div>
        
            {/* {showGraph?<div className={styles.midSection}>

            <div className={styles.rchart}>
                <div className={styles.content}>
                <div>Parameter 1</div>
                <LineChart width={550} height={300} data={arr2}>
                  <XAxis dataKey="name" tick={false} label={{ value: "Date", position: "insideBottom", dy: 10}} height={45} />
                  <YAxis domain={[30,75]} label={{ value: 'Vibration Data', angle: -90, position: 'center' ,paddingRight:'20px', dy: -10}} width={80}/>
                  <ReferenceLine y={45} stroke="red" strokeDasharray="6 6" />
                  <ReferenceLine y={60} stroke="red" strokeDasharray="6 6" />
                  <Tooltip />
                  <CartesianGrid horizontal={false} vertical={false}/>
                  
                  <Line type="monotone" dataKey="uv" stroke={maxThreshold>=arr2[arr2.length-1]["uv"] && arr2[arr2.length-1]["uv"]>=minThreshold?"green":"red"} />
                 
                </LineChart>
                </div>
              </div>

              <div className={styles.rchart}>
                <div className={styles.content}>
                <div>Parameter 2</div>
                <LineChart width={550} height={300} data={arr2}>
                  <XAxis dataKey="name" tick={false} label={{ value: "Date", position: "insideBottom", dy: 10}} height={45} />
                  <YAxis domain={[30,75]} label={{ value: 'Vibration Data', angle: -90, position: 'center' ,paddingRight:'20px', dy: -10}} width={80}/>
                  <ReferenceLine y={45} stroke="red" strokeDasharray="6 6" />
                  <ReferenceLine y={60} stroke="red" strokeDasharray="6 6" />
                  <Tooltip />
                  <CartesianGrid horizontal={false} vertical={false}/>
                  <Line type="monotone" dataKey="uv" stroke={maxThreshold>=arr2[arr2.length-1]["uv"] && arr2[arr2.length-1]["uv"]>=minThreshold?"green":"red"} />
                  
                </LineChart>


    
                 
                </div>
              </div>


              <div className={styles.rchart}>
                <div className={styles.content}>
                <div>Parameter 3</div>
                <LineChart width={550} height={300} data={arr}>
                  <XAxis dataKey="name" tick={false} label={{ value: "Date", position: "insideBottom", dy: 10}} height={45} />
                  <YAxis domain={[30,80]} label={{ value: 'Vibration Data', angle: -90, position: 'center' ,paddingRight:'20px', dy: -10}} width={80}/>
                  <ReferenceLine y={45} stroke="red" strokeDasharray="6 6" />
                  <ReferenceLine y={60} stroke="red" strokeDasharray="6 6" />
                  <Tooltip />
                  <CartesianGrid horizontal={false} vertical={false}/>
                  <Line type="monotone" dataKey="uv" stroke="#8884d8" />
                  <Line type="monotone" dataKey="pv" stroke="#82ca9d" />
                </LineChart>


    
                 
                </div>
              </div>
            
              <div className={styles.rchart}>
                <div className={styles.content}>
                <div>Parameter 4</div>
                <LineChart width={500} height={300} data={arr}>
                
                <XAxis dataKey="name" label={{ value: "Date", position: "insideBottom", dy: 10}} height={45} tick={false} />
                 
                  <YAxis domain={[30,80]} label={{ value: 'Vibration Data', angle: -90, position: 'center' ,paddingRight:'20px', dy: -10}} width={80}/>

                  
                  <ReferenceLine y={40} stroke="red" strokeDasharray="6 6" />
                  <ReferenceLine y={60} stroke="red" strokeDasharray="6 6" />
                  <Tooltip />
                  <CartesianGrid horizontal={false} vertical={false}/>
                  <Line type="monotone" dataKey="uv" stroke="#8884d8" />
                  <Line type="monotone" dataKey="pv" stroke="#82ca9d" />
                </LineChart>

                
    
                 `
                </div>
              </div>

              
             
            </div>:null} */}
            
            {loading && (
          <div className={styles.midSection}>
            <Loader
              type="Puff"
              color="#86a0ff"
              height={300}
              width={300}
              style={{ marginTop: 100 }}
            />
          </div>
        )}
        {graphData.length ? (
          <>
            <div className={styles.midSection}>
              <div className={styles.rchart}>
                <div className={styles.content}>
                  <div className={styles.relChart}>
                    {graphData && <ReliabilityChart data={graphData} />}
                  </div>
                  <div className={styles.compareMission}>
                    <div style={{ display: "flex", alignItems: "center" }}>
                      <AddCircleOutlineIcon /> Compare with other missions
                    </div>
                    {cardData ? (
                      <div className={styles.missionbox}>
                        {/* <div className={styles.missionName}>Mission B</div> */}
                        <MissionSlider
                          missions={cardData}
                          currentMission={currentMission}
