import React, { useState } from 'react';
import Container from '@material-ui/core/Container';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import { Box, CssBaseline, Grid } from '@material-ui/core';
import styles from './pm.module.css';
import OptiQ from './OptiQ';
import optTable from "./OptTable";

const PM = () => {
  const [selectedOption, setSelectedOption] = useState('');
  const [currQ, setCurrQ] = useState('');

  const questions = [
    {
      id: 'beta',
      text: 'Enter beta value:',
      type: 'text',
      required: true,
    },
    {
      id: 'eeta',
      text: 'Enter eeta value:',
      type: 'text',
      required: true,
    },
    {
      id: 'cf',
      text: 'Enter cost of unplanned failure:',
      type: 'text',
      required: true,
    },
    {
      id: 'cp',
      text: 'Enter cost of preventive replacement:',
      type: 'text',
      required: true,
    },
  ];
  const rowDatas = {
    beta: 2.5,
    eeta: 10.8,
    cf: 1000,
    cp: 500,
  };
  
  const handleRadioChange = (event) => {
    setSelectedOption(event.target.value);
  
    switch (event.target.value) {
      case 'option1':
        setCurrQ({ columns: ['beta', 'eeta', 'cf', 'cp'], name: 'age_based' });
        break;
      case 'option2':
        setCurrQ({ columns: ['beta', 'eeta', 'df', 'dp'], name: 'downtime_based' });
        break;
      case 'option3':
        setCurrQ({ columns: ['beta', 'eeta', 'c', 'rt'], name: 'component_group' });
        break;
      case 'option4':
        setCurrQ({ columns: ['beta', 'eeta', 'rt'], name: 'downtime_component_group' });
        break;
      case 'option5':
        setCurrQ({ columns: ['beta', 'eeta', 'cf', 'cp'], name: 'calendar_time' });
        break;
      case 'option6':
        setCurrQ({ columns: ['beta', 'eeta','p'], name: 'risk_target' });
        break;
      case 'option7':
        setCurrQ({ columns: ['beta', 'eeta', 'cf', 'cp'], name: 'Option 7' });
        break;
      default:
        setCurrQ('');
        break;
    }
  };
  
  return (
    <>
      <Container>
        <div className={styles.main}>
          <div className={styles.userSelection}>
            <RadioGroup value={selectedOption} onChange={handleRadioChange}>
              <div className={styles.radioRow}>
                <FormControlLabel
                  value="option1"
                  control={<Radio color="primary" />}
                  label="age_based"
                  name="age_based"
                />
                <FormControlLabel
                  value="option2"
                  control={<Radio color="primary" />}
                  label="AgeBasedReplacementDowntime"
                  name="AgeBasedReplacementDowntime"
                />
                <FormControlLabel
                  value="option3"
                  control={<Radio color="primary" />}
                  label="CalenderTimeBasedReplacemenGroupt1"
                  name="CalenderTimeBasedReplacemenGroupt1"
                />
              </div>
              <div className={styles.radioRow}>
                <FormControlLabel
                  value="option4"
                  control={<Radio color="primary" />}
                  label="CalenderTimeBasedReplacemenGrouptDowntime"
                  name="CalenderTimeBasedReplacemenGrouptDowntime"
                />
                <FormControlLabel
                  value="option5"
                  control={<Radio color="primary" />}
                  label="CalenderTimeBasedReplacement"
                  name="CalenderTimeBasedReplacement"
                />
                <FormControlLabel
                  value="option6"
                  control={<Radio color="primary" />}
                  label="RiskBasedReplacement"
                  name="RiskBasedReplacement"
                />
              </div>
              <div className={styles.radioRow}>
                <FormControlLabel
                  value="option7"
                  control={<Radio color="primary" />}
                  label="Option 7"
                  name="Option 7"
                />
              </div>
            </RadioGroup>
          </div>
 
          <div spacing={3} className={styles.userSelection}>
            {selectedOption && <OptiQ option={selectedOption} name={currQ.name} questions={questions} />}
          </div>
          <div className={styles.userSelection}>
          <OptTable
            columnDefs={currQ.columns?.map((column) => ({ headerName: column, field: column }))}
            rowData={rowDatas} // Provide the row data for the table
            height={200}
          />
        </div>
        </div>

        
      </Container>
    </>
  );
};

export default PM;
