// components/AccessControlHOC.js
import React from 'react';
import { getUserAccessLevel } from './accessUtils';

const AccessControlHOC = (allowedLevels, WrappedComponent) => {
    
  return (props) => {
    const userAccessLevel = getUserAccessLevel();

    // Check if the user's access level is included in the allowedLevels array.
    const isAllowed = allowedLevels.includes(userAccessLevel);

    if (!isAllowed) {
      // Replace this with your preferred way to handle unauthorized access.
      return <div>You are not authorized to access this content.</div>;
    }

    return <WrappedComponent {...props} />;
  };
};

export default AccessControlHOC;
