import React, { useState, useEffect } from "react";
import styles from "./TaskDashboard.module.css";
import {  InputLabel, TextField,makeStyles,Button} from "@material-ui/core";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip,Label,ReferenceLine } from 'recharts';
// import { arr,arr2 } from "./data";
import {
  MuiPickersUtilsProvider,
  KeyboardDatePicker,
} from "@material-ui/pickers";
import MomentUtils from "@date-io/moment";
import Navigation from "../../components/navigation/Navigation";

import CustomSelect from "../../ui/Form/CustomSelect";
import { Autocomplete } from "@material-ui/lab";



const TaskDashboard = () => {
  const [shipOption, setshipOption] = useState([]);
  const [taskOption, settaskOption] = useState([]);
  const [missionOption, setmissionOption] = useState([]);

  useEffect(() => {
    fetch("/task_dash_populate", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    })
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        debugger;
        const mission_data = data["missionData"];
        const taskNames = data["tasks"]
        setmissionOption(mission_data);
        settaskOption(taskNames);      
      });
  }, []);


  const dropDownStyle = makeStyles({
    root: {
      paddingLeft: 10,
      background: "#fff",
      border: "1px solid #0263a1",
      borderRadius: "5px",
      width: "320px",
      minHeight: "40px",
      boxShadow: "2px 3px 5px -1px rgba(0,0,0,0.2)",
    },
    inputRoot:{
      width: '100%'
    },
  });
  const classes=dropDownStyle();
  let arr=[]
  let arr2=[]
  for(let i=0;i<50;i++){
    // const maxDate = Date.now();
    // const timestamp = Math.floor(Math.random() * maxDate);
    arr=[...arr,{name:new Date(new Date(2021, 1, 1).getTime() + Math.random() * (new Date(2022, 1, 1).getTime() - new Date(2021, 1, 1).getTime())).toISOString().slice(0,10),uv:Math.floor(Math.random() * (80 - 30 + 1)) + 30}]

    arr2=[...arr2,{name:new Date(new Date(2021, 1, 1).getTime() + Math.random() * (new Date(2022, 1, 1).getTime() - new Date(2021, 1, 1).getTime())).toISOString().slice(0,10),uv:Math.floor(Math.random() * (75 - 30 + 1)) + 30}]
  }
  const [showGraph,setShowGraph]=useState(false);

  const onSubmitHandler = () => {
    // setGraphData([]);
    // setSubSystem(false);
    // setLoading(true);
    const data = {
      
    };
    // setMission(0);
    // console.log(missionInfo);
    fetch("/rel_estimate", {
      method: "POST",
      body: JSON.stringify({ data: data }),
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    })
      .then((res) => res.json())
      .then((d) => {
        console.log(d);
      });
  };

  const minThreshold=45
  const maxThreshold=60
  return (
    <MuiPickersUtilsProvider utils={MomentUtils}>
      <Navigation />
      <div className={styles.body}>
        <div className={styles.mprofile}>
          {/* <div style={{ width: "300px" }}>
            <InputLabel
              style={{
                fontWeight: "bold",
                color: "black",
                fontSize: "16px",
                marginBottom: "10px",
              }}
            >
              Ship Name
            </InputLabel>
            <CustomSelect fields={['a','b','c','d','e']}/>
              
           
            
          </div> */}
          <div style={{ width: "300px" }}>
            <InputLabel
              style={{
                fontWeight: "bold",
                color: "black",
                fontSize: "16px",
                marginBottom: "10px",
              }}
            >
              Task Name
            </InputLabel>

            <Autocomplete
            classes={classes}
              multiple
              id="tags-standard"
              options={taskOption}
              getOptionLabel={(option) => option.name}
              
              renderInput={(params) => (
                <TextField
                  {...params}
                  InputProps={{...params.InputProps, disableUnderline: true}}
                  variant="standard"
                  // label="Multiple values"
                  // placeholder="Favorites"
                />
              )}
            />
            
          </div>
          
          <div style={{ width: "300px" }}>
            <InputLabel
              style={{
                fontWeight: "bold",
                color: "black",
                fontSize: "16px",
                marginBottom: "10px",
              }}
            >
              Select Mission
            </InputLabel>

            <Autocomplete
            classes={classes}
              multiple
              id="tags-standard"
              options={missionOption}
              getOptionLabel={(option) => option.missionName}
              renderInput={(params) => (
                <TextField
                  {...params}
                  InputProps={{...params.InputProps, disableUnderline: true}}
                  variant="standard"
                  // label="Multiple values"
                  // placeholder="Favorites"
                />
              )}
            />
            
          </div>

          <Button
            variant="contained"
            color="primary"
            style={{
              marginTop: "2rem",
            }}
            onClick={onSubmitHandler} 
          >{console.log(arr[arr.length-1]["uv"]+"hello2")}
            Submit
          </Button>


         
          
        </div>
        
        
        
            {showGraph?<div className={styles.midSection}>

