import React, { useState } from 'react';
import Container from '@material-ui/core/Container';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import { Grid } from '@material-ui/core';
import styles from './pm.module.css';
import OptiQ from './OptiQ';
// import MyComponentToRender from './MyComponentToRender';

const PM = () => {
  const [selectedOption, setSelectedOption] = useState('');

  const handleRadioChange = (event) => {
    setSelectedOption(event.target.value);
  };

  return (
    <Grid spacing={3} className={styles.userSelection}>
      <Container elevation={3} style={{ padding: '20px' }}>
        <RadioGroup value={selectedOption} onChange={handleRadioChange}>
          <div className={styles.radioRow}>
            <FormControlLabel
              value="option1"
              control={<Radio color="primary" />}
              label="AgeBasedReplacement"
            />
            <FormControlLabel
              value="option2"
              control={<Radio color="primary" />}
              label="AgeBasedReplacementDowntime"
            />
            <FormControlLabel
              value="option3"
              control={<Radio color="primary" />}
              label="Option 3"
            />
          </div>
          <div className={styles.radioRow}>
            <FormControlLabel
              value="option4"
              control={<Radio color="primary" />}
              label="Option 4"
            />
            <FormControlLabel
              value="option5"
              control={<Radio color="primary" />}
              label="Option 5"
            />
            <FormControlLabel
              value="option6"
              control={<Radio color="primary" />}
              label="Option 6"
            />
          </div>
          <div className={styles.radioRow}>
            <FormControlLabel
              value="option7"
              control={<Radio color="primary" />}
              label="Option 7"
            />
          </div>
        </RadioGroup>

        {selectedOption && <OptiQ option={selectedOption} />}
      </Container>
    </Grid>
  );
};

export default PM;
