import React, { useEffect, useState } from "react";
import { AgGridReact } from "ag-grid-react";
import "ag-grid-community/dist/styles/ag-grid.css";
import "ag-grid-community/dist/styles/ag-theme-alpine.css";
import { Button, Dialog, DialogTitle, DialogContent, DialogActions } from "@material-ui/core";
import { makeStyles } from "@material-ui/core/styles";
import styles from "./pm.module.css";

const OptTable = ({ columnDefs, rowData, height, name, n }) => {
  const useStyles = makeStyles((theme) => ({
    dialogContainer: {
      borderRadius: theme.spacing(2),
      padding: theme.spacing(2),
    },
    dialogTitle: {
      paddingBottom: theme.spacing(1),
    },
    dialogContent: {
      paddingTop: 0,
      paddingBottom: theme.spacing(2),
    },
  }));
  const [open, setOpen] = useState(false);
  const [tval, setTval] = useState(0);
  const [tval90, setTval90] = useState(0);
  const [tval110, setTval110] = useState(0);
  const [tableData, setTableData] = useState([]);

  const classes = useStyles();
  const handleClose = () => {
    setOpen(false); // Close the dialog
  };

  const handleT = (event) => {
    event.preventDefault();

    // Include the method value in the answers object
    const dataToSend = { ...tableData[0], method: name };
    console.log(dataToSend);

    // Send the answers to the Flask backend
    fetch("/optimize", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(dataToSend),
    })
      .then((response) => response.json())
      .then((data) => {
        // Handle the response from the backend
        console.log(data);
        setTval(data.t);
        setTval90(data.t - 0.1 * data.t);
        setTval110(data.t + 0.1 * data.t);
        // Set the tableData state with the row data when the form is submitted
      })
      .catch((error) => {
        // Handle any errors
        console.error(error);
      });
    setOpen(true);
  };

  useEffect(() => {
    const initialTableData = n > 0 ? new Array(n).fill({}) : [rowData]; // Create an array of 'n' empty objects if 'n' is greater than 0, otherwise create an array with a single empty object
    setTableData(initialTableData);
    console.log(initialTableData)
  }, [n]);

  return (
    <div className="ag-theme-alpine" style={{ height: height, width: "100%", position: "relative" }}>
      <AgGridReact
        columnDefs={columnDefs}
        rowData={tableData}
        defaultColDef={{
          flex: 1,
          resizable: true,
          filter: true,
          sortable: true,
          editable: true,
        }}
      />
      <Button className={styles.btn} type="submit" variant="contained" color="primary" onClick={handleT}>
        Submit
      </Button>
      <Dialog open={open} onClose={handleClose} maxWidth="xs" fullWidth>
        <div className={classes.dialogContainer}>
          <DialogTitle className={classes.dialogTitle}>Optimization Result</DialogTitle>
          <DialogContent className={classes.dialogContent}>
            <p>t value: {tval}</p>
            <p>t value (90%): {tval90}</p>
            <p>t value (110%): {tval110}</p>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose} color="primary">
              Close
            </Button>
          </DialogActions>
        </div>
      </Dialog>
    </div>
  );
};

export default OptTable;
