                      <YAxis
                        domain={[0, 100]}
                        label={{
                          value: `${param.parameterName} Data`,
                          angle: -90,
                          position: "center",
                          paddingRight: "20px",
                          dy: -10,
                        }}
                        width={80}
                      />
                      <CartesianGrid horizontal={false} vertical={false} />

                      <Line
                        type="monotone"
                        dataKey="value"
                        stroke={crossingThreshold ? "red" : "green"}
                      />

                      <ReferenceLine
                        y={minThreshold}
                        stroke="gray"
                        strokeDasharray="6 6"
                      />
                      <ReferenceLine
                        y={maxThreshold}
                        stroke="gray"
                        strokeDasharray="6 6"
                      />

                      <Tooltip />
                    </LineChart>
                  </div>
                </div>
              );
            })}

