import React, { useState } from 'react';
import Container from '@material-ui/core/Container';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import { Box, CssBaseline, Grid } from '@material-ui/core';
import styles from './pm.module.css';
import OptiQ from './OptiQ';

const DataTable = ({ columns, data }) => {
  return (
    <table>
      <thead>
        <tr>
          {columns.map((column) => (
            <th key={column}>{column}</th>
          ))}
        </tr>
      </thead>
      <tbody>
        <tr>
          {columns.map((column) => (
            <td key={column}>{data[column]}</td>
          ))}
        </tr>
      </tbody>
    </table>
  );
};

const PM = () => {
  const [selectedOption, setSelectedOption] = useState('');
  const [currQ, setCurrQ] = useState('');
  const [formData, setFormData] = useState({}); // Store form data

  const handleRadioChange = (event) => {
    setSelectedOption(event.target.value);
    setCurrQ(event.target.name);
  };

  const handleFormSubmit = (data) => {
    setFormData((prevFormData) => ({
      ...prevFormData,
      [currQ]: data,
    }));
  };

  const questions = [
    {
      id: 'beta',
      text: 'Enter beta value:',
      type: 'text',
      required: true,
    },
    {
      id: 'eeta',
      text: 'Enter eeta value:',
      type: 'text',
      required: true,
    },
    {
      id: 'cf',
      text: 'Enter cost of unplanned failure:',
      type: 'text',
      required: true,
    },
    {
      id: 'cp',
      text: 'Enter cost of preventive replacement:',
      type: 'text',
      required: true,
    },
  ];

  return (
    <>
      <Container>
        <div className={styles.main}>
          <div className={styles.userSelection}>
            <RadioGroup value={selectedOption} onChange={handleRadioChange}>
              <div className={styles.radioRow}>
                <FormControlLabel
                  value="option1"
                  control={<Radio color="primary" />}
                  label="AgeBasedReplacement"
                  name="AgeBasedReplacement"
                />
                {/* ...rest of the FormControlLabel components */}
              </div>
              {/* ...rest of the radio rows */}
            </RadioGroup>
          </div>

          <div spacing={3} className={styles.userSelection}>
            {selectedOption && (
              <OptiQ
                option={selectedOption}
                name={currQ}
                questions={questions}
                onSubmit={handleFormSubmit} // Pass the submit handler to OptiQ component
              />
            )}
          </div>

          {/* Render the DataTable component based on the selected option */}
          {selectedOption === 'option1' && (
            <div className={styles.userSelection}>
              <DataTable columns={Object.keys(formData)} data={formData} />
            </div>
          )}
        </div>
      </Container>
    </>
  );
};

export default PM;
