import React, { useState } from 'react';
import Container from '@material-ui/core/Container';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import { CssBaseline, Grid } from '@material-ui/core';
import styles from './pm.module.css';
import OptiQ from './OptiQ';

const PM = () => {
  const [selectedOption, setSelectedOption] = useState('');
  const [currQ, setCurrQ] = useState('');

  const handleRadioChange = (event) => {
    setSelectedOption(event.target.value);
    setCurrQ(event.target.name);
  };
  const questions = [
    {
      id: 'beta',
      text: 'Enter beta value:',
      type: 'text',
      required: true,
    },
    {
      id: 'eeta',
      text: 'Enter eeta value:',
      type: 'text',
      required: true,
    },
    {
      id: 'cf',
      text: 'Enter cost of unplanned failure:',
      type: 'text',
      required: true,
    },
    {
      id: 'cp',
      text: 'Enter cost of preventive replacement:',
      type: 'text',
      required: true,
    },
  ];
  
  // Usage
 
  

  return (
    <>
    <div style={{display: "block", justi}}>
    <Grid container spacing={3} className={styles.userSelection}>
        <RadioGroup value={selectedOption} onChange={handleRadioChange}>
          <div className={styles.radioRow}>
            <FormControlLabel
              value="option1"
              control={<Radio color="primary" />}
              label="AgeBasedReplacement"
              name="AgeBasedReplacement"
            />
            <FormControlLabel
              value="option2"
              control={<Radio color="primary" />}
              label="AgeBasedReplacementDowntime"
              name="AgeBasedReplacementDowntime"
            />
            <FormControlLabel
              value="option3"
              control={<Radio color="primary" />}
              label="CalenderTimeBasedReplacemenGroupt1"
              name="CalenderTimeBasedReplacemenGroupt1"
            />
          </div>
          <div className={styles.radioRow}>
            <FormControlLabel
              value="option4"
              control={<Radio color="primary" />}
              label="CalenderTimeBasedReplacemenGrouptDowntime"
              name="CalenderTimeBasedReplacemenGrouptDowntime"
            />
            <FormControlLabel
              value="option5"
              control={<Radio color="primary" />}
              label="CalenderTimeBasedReplacement"
              name="CalenderTimeBasedReplacement"
            />
            <FormControlLabel
              value="option6"
              control={<Radio color="primary" />}
              label="RiskBasedReplacement"
              name="RiskBasedReplacement"
            />
          </div>
          <div className={styles.radioRow}>
            <FormControlLabel
              value="option7"
              control={<Radio color="primary" />}
              label="Option 7"
              name="Option 7"
            />
          </div>
        </RadioGroup>
      {/* <CssBaseline /> */}
    </Grid>
 
    <Grid spacing={3} className={styles.userSelection}>

    {selectedOption && <OptiQ option={selectedOption} name={currQ} questions={questions}/>}
    </Grid>
    </div>
    </>
  );
};

export default PM;
