import React, { useState } from "react";
import { TextField, Button, Dialog, DialogTitle, DialogContent, DialogActions } from "@material-ui/core";
import styles from "./pm.module.css";
import OptTable from "./OptTable";

const OptiQ = ({ questions, name, currQ }) => {
  const [answers, setAnswers] = useState({});
  const [n, setN] = useState(0); // State to store the value of 'n'

  const handleAnswerChange = (event, questionId) => {
    const { value } = event.target;
    setAnswers((prevAnswers) => ({
      ...prevAnswers,
      [questionId]: parseFloat(value),
    }));
  };

  const handleAddRow = (event) => {
    event.preventDefault();
    const nValue = parseFloat(answers["n"] || "0");
    setN(nValue);
  };

  return (
    <>
      <div className={styles.mainOp}>
        <div className={styles.userSele}>
          <h3>{name} Questions</h3>
          <form>
            {questions?.map((question) => (
              <div key={question.id}>
                <label>{question.text}</label>
                {question.type === "text" && (
                  <TextField
                    type="number"
                    value={answers[question.id] || ""}
                    onChange={(event) => handleAnswerChange(event, question.id)}
                    required={question.required}
                  />
                )}
              </div>
            ))}
            <Button type="submit" variant="contained" color="primary" onClick={handleAddRow}>
              Add Row
            </Button>
          </form>
        </div>
        <div className={styles.OptTable}>
          <OptTable
            columnDefs={currQ.columns?.map((column) => ({ headerName: column, field: column }))}
            rowData={answers} // Use answers as the rowData for the OptTable
            height={200}
            answers={answers}
            name={name}
            n={n} // Pass the 'n' value as a prop to the OptTable component
          />
        </div>
      </div>
    </>
  );
};

export default OptiQ;
