import React, { useState, useCallback } from "react";
import Navigation from "../../../components/navigation/Navigation";
import styles from "./AddData.module.css";
import UserSelection from "../../../ui/userSelection/userSelection";
import { Button, makeStyles } from "@material-ui/core";
import TreeComponent from "../../../components/sortableTree/SortableTree";
import { useSelector, useDispatch } from "react-redux";
import { treeDataActions } from "../../../store/TreeDataStore";
import AutoSelect from "../../../ui/Form/AutoSelect";
import { AgGridColumn } from "ag-grid-react";
import Table from "../../../ui/Table/Table";
import { v4 as uuid } from "uuid";
import Loader from "react-loader-spinner";
import { useDropzone } from "react-dropzone";
import CustomizedSnackbars from "../../../ui/CustomSnackBar";

const useStyles = makeStyles({
  buttons: {
    margin: 5,
    minWidth: 170,
    float: "right",
  },
  align: {
    marginBottom: 10,
  }
});

const AddData = (props) => {
  const dispatch = useDispatch();
  const currentSelection = useSelector(
    (state) => state.userSelection.currentSelection
  );
  let fData = useSelector((state) => state.treeData.treeData);

  const onLoadTreeStructure = () => {
    fetch("/fetch_system", {
      method: "POST",
      body: JSON.stringify({
        system: currentSelection["equipmentName"],
        ship_name: currentSelection["shipName"],
      }),
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    })
      .then((res) => res.json())
      .then((d) => {
        let treeD = d["treeD"];
        let failureModes = d["failureMode"];
        dispatch(
          treeDataActions.setTreeData({
            treeData: treeD,
            failureModes: failureModes,
          })
        );
      });
  };

  const [isloading, setLoading] = useState(false);
  const [dataRows, setDataRows] = useState([]);
  const [selectedComponent, setComponent] = useState(null);
  const [paramData, setParamData] = useState([]);

  const selectOnChange = (e, value) => {
    setDataRows([]);
    if (value) {
      setLoading(true);
      fetch("/fetch_params", {
        method: "POST",
        body: JSON.stringify({
          ComponentId: value.id,
        }),
        headers: {
          "Content-Type": "application/json",
          Accept: "application/json",
        },
      })
        .then((res) => res.json())
        .then((d) => {
          setParamData(d);
        });
      setComponent(value);
      setLoading(false);
    }
  };

  const saveParamData = () => {
    fetch("/save_condition_monitoring", {
      method: "POST",
      body: JSON.stringify({
        flatData: dataRows,
        dtype: "insertParamData",
      }),
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        setSnackBarMessage({
          severity: "success",
          message: data.message,
          showSnackBar: true,
        });
      })
      .catch((error) => {
        setSnackBarMessage({
          severity: "error",
          message: "Some Error Occured. " + error,
          showSnackBar: true,
        });
      });
  };

  const DataColumnDefs = [
    <AgGridColumn
      field="date"
      headerName="Date"
      headerTooltip="Date"
      cellEditor="datePicker"
      editable={true}
    />,
    <AgGridColumn
      field="parameterName"
      headerName="Parameter"
      headerTooltip="Parameter"
      cellEditor="agSelectCellEditor"
      cellEditorParams={{
        values: paramData.map((data) => data.name),
      }}
      editable={true}
      onCellValueChanged={(params) => {
        let parameter = paramData.filter(
          (d) => d.name === params.data.parameterName
        );
        params.data.paramId = parameter[0].id;
      }}
    />,
    <AgGridColumn
      field="value"
      headerName="Value"
      headerTooltip="Value"
      editable={true}
    />,
  ];

  const addRow = () => {
    let newRow = {
      componentId: selectedComponent.id,
      id: uuid(),
      date: "",
      parameterName: "",
      paramId: "",
      value: "",
    };
    setDataRows([...dataRows, newRow]);
  };

  const onDrop = useCallback((acceptedFiles) => {
    const formData = new FormData();
    formData.append("File", acceptedFiles[0]);
    fetch("/add_data", {
      method: "POST",
      body: formData,
    })
      .then((response) => response.json())
      .then((result) => {
        console.log("Success:", result);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  }, []);

  const { getRootProps, getInputProps } = useDropzone({ onDrop });

  // Snackbar
  const [SnackBarMessage, setSnackBarMessage] = useState({
    severity: "error",
    message: "This is awesome",
    showSnackBar: false,
  });

  const onHandleSnackClose = () => {
    setSnackBarMessage({
      severity: "error",
      message: "Please Add Systemss",
      showSnackBar: false,
    });
  };

  const classes = useStyles();

  return (
    <>
      <Navigation />
      <div className={styles.userSelection}>
        <UserSelection />
        <div>
          {/* <div {...getRootProps()}>
            <input {...getInputProps()} />
            <Button
              className={classes.buttons}
              variant="contained"
              color="primary"
            >
              Import File
            </Button>
          </div> */}
          <Button
            className={classes.buttons}
            onClick={onLoadTreeStructure}
            variant="contained"
            color="primary"
          >
            Submit
          </Button>
        </div>
      </div>

      <div className={styles.content}>
        <div className={styles.tree}>
          <div className={styles.treeChild}>
            <TreeComponent height="600px"></TreeComponent>
          </div>
        </div>
        <div className={styles.rightSection}>
          
          {/* <div className={styles.userSelection} >
          <div className={styles.selectComponent}>
            Select Component
            <AutoSelect
              fields={fData}
              onChange={selectOnChange}
              value={selectedComponent}
            ></AutoSelect>
          </div>
            <div>
              <Button
                className={classes.buttons}
                variant="contained"
                color="primary"
              >
                Import File
              </Button>
              <div {...getRootProps()}>
                <input {...getInputProps()} />
              </div>
            </div>
          </div> */}
          <div className={styles.userSelection}>
  <div className={styles.selectContainer}>
    <div className={styles.selectC}>
      Select Component
      <AutoSelect
        fields={fData}
        onChange={selectOnChange}
        value={selectedComponent}
      ></AutoSelect>
    </div>
    <div className={styles.importBtnContainer}>
      <Button
        className={classes.buttons}
        variant="contained"
        color="primary"
      >
        Import File
      </Button>
      <div {...getRootProps()}>
        <input {...getInputProps()} />
      </div>
    </div>
  </div>
</div>


          {isloading ? (
            <Loader
              type="Puff"
              color="#86a0ff"
              height={300}
              width={300}
              style={{ marginTop: 100 }}
            />
          ) : (
            <div className={styles.table}>
              <Table
                columnDefs={DataColumnDefs}
                rowData={dataRows}
                tableUpdate={(rows) => {
                  console.log(rows);
                }}
                height={300}
              />
              <Button variant="contained" color="primary" onClick={addRow}>
                + Add Row
              </Button>
            </div>
          )}
        </div>
      </div>

      {SnackBarMessage.showSnackBar && (
        <CustomizedSnackbars
          message={SnackBarMessage}
          onHandleClose={onHandleSnackClose}
        />
      )}
    </>
  );
};

export default AddData;
