import { Button } from "@material-ui/core";
import { AgGridColumn } from "ag-grid-react";
import CustomSelect from "../../../ui/Form/CustomSelect";
import Table from "../../../ui/Table/Table";
import styles from "../SystemConfiguration.module.css";
import { useDispatch, useSelector } from "react-redux";
import { v4 as uuid } from "uuid";
import AddIcon from "@material-ui/icons/Add";
import DeleteIcon from "@material-ui/icons/Delete";
import { useEffect, useState } from "react";
import RenderParallelComponent from "../redundancy/RenderParallelComponent";
const FailureMode = (props) => {
  const systemData = useSelector((state) => state.treeData.treeData);
  const failureModesData = useSelector((state) => state.treeData.failureModes);
  const equipments = systemData.map(
    (eqpt) => eqpt.name + "-" + eqpt.parentName
  );
  let ParallelIds = [];

  const setParallelIds = (d) => {
    ParallelIds = d;
  };
  const rowData = systemData.map((element, index) => {
    return {
      EquipmentName: element.name,
      id: uuid(),
      eqId: element.id,
      fixFailureMode: "",
    };
  });
  const FMcolumnDefs = [
    <AgGridColumn
      field="EquipmentName"
      headerName="Equipment Name"
      headerTooltip="Equipment Name"
      width={300}
      editable={false}
    />,
    <AgGridColumn
      field="fixFailureMode"
      headerName="Failure Mode"
      headerTooltip="Failure Mode"
      width={300}
      editable={true}
      cellEditor="agSelectCellEditor"
      cellEditorParams={{
        values: failureModesData,
      }}
    />
  ];

  const AddRow = () => {
    const defaultRow = [
      {
        EquipmentName: "",
      id: uuid(),
      eqId: "",
      fixFailureMode: "",
      },
    ];
    gridApi.applyTransaction({
      add: defaultRow,
    });
  };
  const deleteRows = () => {
    const selectedRows = gridApi.getSelectedRows();
    gridApi.applyTransaction({ remove: selectedRows });
    // console.log(selectedRows);
  };
  const updateFinalRowData = (allRows, rowId) => {
    debugger;
    props.tableUpdate(allRows);
  };
  return (
    <div className={styles.systemTable}>
      <Table
        columnDefs={FMcolumnDefs}
        rowData={rowData}
        tableUpdate={updateFinalRowData}
      />
      <div className={styles.tableFooter}>
            <Button
              variant="contained"
              startIcon={<AddIcon />}
              color="secondary"
              onClick={() => AddRow()}
            >
              Add Row
            </Button>
            <Button
              style={{ marginLeft: 10 }}
              variant="contained"
              startIcon={<DeleteIcon />}
              color="secondary"
              onClick={() => deleteRows()}
            >
              Delete Rows
            </Button>
          </div>
    </div>
  );
};
export default FailureMode;
