import React, { useEffect, useState } from "react";
import NewModule from "../../components/module/NewModule";
import Navigation from "../../components/navigation/Navigation";
import UserSelection from "../../ui/userSelection/userSelection";
import styles from "./UserSelectionConfiguration.module.css";
import { AgGridColumn } from "ag-grid-react";
import Table from "../../ui/Table/DataManagerTable";
import { Button, makeStyles, Grid } from "@material-ui/core";
import AddIcon from "@material-ui/icons/Add";
import { v4 as uuidv4 } from "uuid";
import DeleteIcon from "@material-ui/icons/Delete";
import CustomizedSnackbars from "../../ui/CustomSnackBar";

//From Add new equipment form
import { userActions } from "../../store/ApplicationVariable";
import { useDispatch, useSelector } from "react-redux";
import { treeDataActions } from "../../store/TreeDataStore";
import classes from "./EqptStructuring.module.css";
import LabelToolTip from "../../components/main/EqptStructuring/LabelToolTip/LabelToolTip";
// import Tree from "./Tree";
// import TreeComponent from "../../sortableTree/SortableTree";
import CustomTextInput from "../../ui/Form/CustomTextInput";
import CustomSelect from "../../ui/Form/CustomSelect";
import AutoSelect from "../../ui/Form/AutoSelect";
import { useFormik } from "formik";
import { v4 as uuid } from "uuid"; 

const SystemStyles = makeStyles({
  formControl: {
    margin: "2rem",
    minWidth: 200,
  },
  Submit: {
    margin: "2rem",
  },
  buttons: {
    minWidth: 150,
    marginLeft: 10,
    marginTop: 15,
    float: "right",
  },
});
const UserSelectionConfiguration = (props) => {
  const SystemClasses = SystemStyles();
  const dispatch = useDispatch();
  const selectedInputs = useSelector(
    (state) => state.userSelection.currentSelection
  );
//   const [gridApi, setGridApi] = useState(null);
  const [SnackBarMessage, setSnackBarMessage] = useState({
    severity: "error",
    message: "This is awesome",
    showSnackBar: false,
  });
  const onHandleSnackClose = () => {
    setSnackBarMessage({
      severity: "error",
      message: "Close",
      showSnackBar: false,
    });
  };


  const formik = useFormik({
    initialValues: {
      platform: "",
      platformType: "",
      system: "",
      systemType: "",
    },
    //validationSchema: validationSchema,
    onSubmit: (values) => {
      // alert(JSON.stringify(values, null, 2));
      const { platform, platformType, system, systemType } = values;
      console.log(selectedInputs);
      const platformId = uuid();
      const systemId = uuid();
      debugger;
      const treeNodes = [
        {
          name: system,
          id: systemId,
          eqType: systemType,
          parentName: selectedInputs["shipName"],
          parentId: null,
          parent: null,
          children: [],
          lmu: 1,
          command: selectedInputs["command"],
          department: selectedInputs["department"],
          shipCategory: selectedInputs["shipCategory"],
          shipClass: selectedInputs["shipClass"],
          shipName: selectedInputs["shipName"],
        },
      ];
      const updateEqStore = {
        equipmentName: system,
        equipmentCode: systemType,
      };
      const filteredData = {
        equipmentName: [system],
        equipmentCode: [systemType],
      };
      dispatch(treeDataActions.addElement({ data: treeNodes }));
      dispatch(
        userActions.onAddingEquipmentName({
          selectedData: updateEqStore,
          filteredData: filteredData,
        })
      );
      setDisableButton(true);
    },
  });



  //Save Button Handler
 

  return (
    <>
      <Navigation />
      <div className={styles.body}>
        {/* <div className={styles.module}>
          <NewModule />
        </div> */}
        <div className={styles.user}>
          <Button
            variant="contained"
            color="primary"
            className={SystemClasses.buttons}
            onClick={onSaveButtonClickHandler}
          >
            Save
          </Button>
        </div>
        {/* <div className={styles.user}><UserSelection /></div> */}
        <div className={styles.table}>
          {/* <Table
            rowData={rowState}
            columnDefs={MProfileColumns}
            tableUpdate={setFinalTableData}
            setGrid={setGridApi}
            gridApi={gridApi}
          /> */}
         <div className={classes.form}>
      <div className={classes.header}>Add New Equipment</div>
        <form style={{ width: "100%" }} onSubmit={formik.handleSubmit}>
          <div className={classes.formrow1}>
            <div className={classes.field1}>
              <LabelToolTip label="System Name" info="Info" />
              <CustomTextInput
                className={classes.fullWidth}
                id="system"
                name="system"
                value={formik.values.system}
                onChange={formik.handleChange}
                disabled={disableButton}
              ></CustomTextInput>
            </div>
            <div className={classes.field1}>
              <LabelToolTip label="System Type" info="Info" />
              <CustomTextInput
                className={classes.fullWidth}
                id="systemType"
                name="systemType"
                value={formik.values.systemType}
                onChange={formik.handleChange}
                disabled={disableButton}
              ></CustomTextInput>
            </div>
          </div>
          <div className={classes.parent}>
            <div style={{ width: "400px" }}>
              <LabelToolTip label="Parent Component" />
              <AutoSelect
                fields={fData}
                onChange={parentOnChange}
                value={parentFiledValue}
              ></AutoSelect>
            </div>
            <div style={{ marginTop: "20px",marginRight:'2%' }}>
              <Button
                className={classesButton.root}
                variant="contained"
                color="primary"
                type="reset"
                onClick={clearForm}
              >
                Clear
              </Button>
              {!disableButton && (
                <Button variant="contained" color="primary" type="submit">
                  Update
                </Button>
              )}
            </div>
          </div>
        </form>
        <div className={classes.formrow2}>
          {/* //onSubmit={updateChildTree} ref={childForm} */}

          {/* This is the end */}
          <form onSubmit={updateChildTree}>
            <div className={classes.child}>
              <Grid container>
                <Grid container item spacing={4}>
                  <Grid item xs={5}>
                    <LabelToolTip label="Child Component Name" />
                  </Grid>
                  <Grid item xs={5}>
                    <LabelToolTip label="Part Code" />
                  </Grid>
                  <Grid item xs={2}></Grid>
                </Grid>
                {childInputFields.map((child, item) => {
                  return (
                    <Grid container item key={item} spacing={4}>
                      <Grid item xs={5} style={{ marginTop: "10px" }}>
                        <CustomTextInput
                          className={classes.fullWidth}
                          name="childName"
                          // id="childName"
                          value={child.childName}
                          onChange={(e) => handleChildChange(item, e)}
                        />
                      </Grid>
                      <Grid item xs={5} style={{ marginTop: "10px" }}>
                        <CustomTextInput
                          className={classes.fullWidth}
                          name="childPartId"
                          // id="partId"
                          value={child.childPartId}
                          onChange={(e) => handleChildChange(item, e)}
                        />
                      </Grid>
                      <Grid item xs={2} style={{ marginTop: "35px" }}>
                        <DeleteIcon
                          fontSize="medium"
                          onClick={() => {
                            onDeleteChildField(item);
                          }}
                        />
                      </Grid>
                    </Grid>
                  );
                })}
              </Grid>
            </div>
            <div
              style={{
                float: "right",
                marginTop: "30px",
                marginBottom: "30px",
              }}
            >
              <Button
                variant="contained"
                color="primary"
                //onClick={updateChildTree}
                type="submit"
                disabled={!(childInputFields.length > 0)}
              >
                Update All
              </Button>
              <Button
                style={{ marginLeft: "20px" }}
                variant="contained"
                color="primary"
                onClick={onAddNewChildField}
              >
                Add
              </Button>
            </div>
          </form>
        </div>
      </div>
          {/* <div className={styles.tableFooter}>
            <Button
              variant="contained"
              startIcon={<AddIcon />}
              color="secondary"
              onClick={() => AddRow()}
            >
              Add Row
            </Button>
            <Button
              style={{ marginLeft: 10 }}
              variant="contained"
              startIcon={<DeleteIcon />}
              color="secondary"
              onClick={() => deleteRows()}
            >
              Delete Rows
            </Button>
          </div> */}
        </div>
      </div>
      {SnackBarMessage.showSnackBar && (
        <CustomizedSnackbars
          message={SnackBarMessage}
          onHandleClose={onHandleSnackClose}
        />
      )}
    </>
  );
};

export default UserSelectionConfiguration;
