import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  taskData: []
};

const taskData = createSlice({
  name: "taskData",
  initialState: initialState,
  reducers: {
    onLoad(state, action) {
      const data = action.payload.taskData;
      state.userSelection = {
        ...state.taskData,
        ...data,
      };
    },
    // end of reducers
  },
});

export default taskData;
export const taskActions = taskData.actions;
