import React, { useState } from 'react';
import Container from '@material-ui/core/Container';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import { Box, CssBaseline, Grid } from '@material-ui/core';
import styles from './pm.module.css';
import OptiQ from './OptiQ';
import Table from "../../ui/Table";

const PM = () => {
  const [selectedOption, setSelectedOption] = useState('');
  const [currQ, setCurrQ] = useState('');

  const questions = [
    {
      id: 'beta',
      text: 'Enter beta value:',
      type: 'text',
      required: true,
    },
    {
      id: 'eeta',
      text: 'Enter eeta value:',
      type: 'text',
      required: true,
    },
    {
      id: 'cf',
      text: 'Enter cost of unplanned failure:',
      type: 'text',
      required: true,
    },
    {
      id: 'cp',
      text: 'Enter cost of preventive replacement:',
      type: 'text',
      required: true,
    },
  ];

  const handleRadioChange = (event) => {
    setSelectedOption(event.target.value);

    switch (event.target.value) {
      case 'option1':
        setCurrQ({ columns: ['beta', 'eeta', 'cf', 'cp'], name: 'AgeBasedReplacement' });
        break;
      case 'option2':
        setCurrQ({ columns: ['beta', 'eeta', 'cf', 'cp'], name: 'AgeBasedReplacementDowntime' });
        break;
      case 'option3':
        setCurrQ({ columns: ['beta', 'eeta', 'cf', 'cp'], name: 'CalenderTimeBasedReplacemenGroupt1' });
        break;
      case 'option4':
        setCurrQ({ columns: ['beta', 'eeta', 'cf', 'cp'], name: 'CalenderTimeBasedReplacemenGrouptDowntime' });
        break;
      case 'option5':
        setCurrQ({ columns: ['beta', 'eeta', 'cf', 'cp'], name: 'CalenderTimeBasedReplacement' });
        break;
      case 'option6':
        setCurrQ({ columns: ['beta', 'eeta', 'cf', 'cp'], name: 'RiskBasedReplacement' });
        break;
      case 'option7':
        setCurrQ({ columns: ['beta', 'eeta', 'cf', 'cp'], name: 'Option 7' });
        break;
      default:
        setCurrQ('');
        break;
    }
  };

  return (
    <Container>
      <div className={styles.main}>
        <div className={styles.userSelection}>
          <RadioGroup value={selectedOption} onChange={handleRadioChange}>
            <div className={styles.radioRow}>
              <FormControlLabel
                value="option1"
                control={<Radio color="primary" />}
                label="AgeBasedReplacement"
                name="AgeBasedReplacement"
              />
              {/* ...rest of the FormControlLabel components */}
            </div>
            {/* ...rest of the radio rows */}
          </RadioGroup>
        </div>

        <div spacing={3} className={styles.userSelection}>
          {selectedOption && <OptiQ option={selectedOption} name={currQ} questions={questions} />}
        </div>
      </div>

      <div className={styles.formField}>
        <label htmlFor="parameters">Define parameters</label>
        <Table
          columnDefs={currQ.columns?.map((column) => ({ headerName: column, field: column }))}
          rowData={[]} // Provide the row data for the table
          // tableUpdate={updateParameterRowData} // Uncomment if you have an update function for row data
          height={200}
        />
      </div>
    </Container>
  );
};

export default PM;
