import React, { useEffect, useState } from "react";
import styles from "./SignIn.module.css";
import {
  Paper,
  makeStyles,
  FormGroup,
  Switch,
  Button,
  FormControlLabel,
  Checkbox,
} from "@material-ui/core";
import { Link } from "react-router-dom";
import CustomizedSnackbars from "../../ui/CustomSnackBar";

const InputStyles = makeStyles({
  root: {
    margin: "15px 0px 5px 0px",
    paddingRight: 10,
    paddingLeft: 10,
    background: "#ebebeb",
    borderRadius: "5px",
    height: 40,
    width: "70%",
    boxShadow: "2px 3px 5px -1px rgba(0,0,0,0.2)",
  },
  label: {
    fontWeight: 600,
  },
});

const SignIn = (props) => {
  const [toggles, setToggles] = useState({
    L1: false,
    L2: false,
    L3: false,
    L4: false,
    L5: false,
  });

  const handleToggleChange = (toggleName) => (event) => {
    setToggles({ ...toggles, [toggleName]: event.target.checked });
  };

  useEffect(() => {
    if (props.loggedIn) {
      props.history.push("/");
    }
  });

  const [keepLogin, setKeepLogin] = useState(false);
  const InputClasses = InputStyles();
  const [userName, setUserName] = useState("");
  const [password, setPassword] = useState("");

  const [showSnackBar, setShowSnackBar] = useState(false);
  const [SnackBarMessage, setSnackBarMessage] = useState({
    severity: "error",
    message: "",
    showSnackBar: false,
  });

  const onHandleSnackClose = () => {
    setSnackBarMessage({
      severity: "error",
      message: "",
      showSnackBar: false,
    });
  };

  const Login = () => {
    if (userName == "admin" && password == "admin") {
      props.setLoggedIn(true);
      props.history.push("/");
    } else {
      setSnackBarMessage({
        severity: "error",
        message: "Enter Correct Login details",
        showSnackBar: true,
      });
    }
  };
  return (
    <div className={styles.container}>
      <Paper className={styles.SignInPaper} elevation={5}>
        <div>
          <img src="/netra-logo.png" width={60} height={60} />
          <div className={styles.netra}>NETRA</div>
        </div>
        <div style={{ textAlign: "center" }}>
          <h5 style={{ margin: 0 }}>Welcome</h5>
          <h6 style={{ margin: 0 }}>Sign in to your account</h6>
        </div>
        <FormGroup>
          <FormControlLabel
            control={
              <Switch
                checked={toggles.L1}
                onChange={handleToggleChange("L1")}
              />
            }
            label="L1 - SHIP HOD"
          />
          <FormControlLabel
            control={
              <Switch
                checked={toggles.L2}
                onChange={handleToggleChange("L2")}
              />
            }
            label="L2 - SHIP CO"
          />
          <FormControlLabel
            control={
              <Switch
                checked={toggles.L3}
                onChange={handleToggleChange("L3")}
              />
            }
            label="L3 - FLEET"
          />
          <FormControlLabel
            control={
              <Switch
                checked={toggles.L4}
                onChange={handleToggleChange("L4")}
              />
            }
            label="L4 - NHQ"
          />
          <FormControlLabel
            control={
              <Switch
                checked={toggles.L5}
                onChange={handleToggleChange("L5")}
              />
            }
            label="L5 - INSMA"
          />
        </FormGroup>
        <Button
          variant="contained"
          style={{ backgroundColor: "#1c4199", color: "white" }}
          onClick={() => Login()}
        >
          Sign In
        </Button>
      </Paper>
      {SnackBarMessage.showSnackBar && (
        <CustomizedSnackbars
          message={SnackBarMessage}
          onHandleClose={onHandleSnackClose}
        />
      )}
    </div>
  );
};

export default SignIn;
