import React, { useEffect, useState } from "react";
import {
  InputLabel,
  TextField,
  makeStyles,
  Button,
} from "@material-ui/core";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ReferenceLine,
} from "recharts";
import {
  MuiPickersUtilsProvider,
  KeyboardDatePicker,
} from "@material-ui/pickers";
import MomentUtils from "@date-io/moment";
import Navigation from "../../components/navigation/Navigation";
import CustomSelect from "../../ui/Form/CustomSelect";
import { Autocomplete } from "@material-ui/lab";
import { useDispatch, useSelector } from "react-redux";
import { userActions } from "../../store/ApplicationVariable";
import CustomizedSnackbars from "../../ui/CustomSnackBar";

const useStyles = makeStyles({
  inputLabel: {
    fontWeight: "bold",
    color: "black",
    fontSize: "16px",
    marginBottom: "10px",
  },
});

const CDashboard = () => {
  const dispatch = useDispatch();
  const classes = useStyles();

  const [userSelectionData, setUserSelectionData] = useState([]);
  const [uniqueEqIds, setUniqueEqIds] = useState([]);
  const [minMax, setMinMax] = useState([]);
  const [selectedEqName, setEquipmentName] = useState([]);
  const [paramOptions, setParamOptions] = useState([]);
  const [selectedShipName, setShipName] = useState([]);
  const [selectedParameterName, setParameterName] = useState([]);
  const [eqDataOption, setEqDataOption] = useState([]);
  const [paramData, setParamData] = useState([]);
  const [currMinMax, setCurrMinMax] = useState([]);
  const [showGraph, setShowGraph] = useState(false);

  const customSelectData = useSelector(
    (state) => state.userSelection.userSelection
  );

  useEffect(() => {
    fetch("/cm_dashboard", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        const params = data.parameters;
        setMinMax([...params]);
        const userSelection = data.user_selection.data;
        const eqData = data.user_selection.eqData;
        const eqIds = data.user_selection.uniq_eq_data;
        setUniqueEqIds(eqIds);
        const shipName = userSelection.map((x) => x.shipName);
        setUserSelectionData(eqData);
        dispatch(
          userActions.onChangeLoad({ filteredData: { shipName: shipName } })
        );
      });
  }, []);

  useEffect(() => {
    const filteredArray = minMax.filter((item) =>
      selectedEqName.some((selected) => item.equipment_id === selected.id)
    );

    const filteredNames = filteredArray.map((item) => item.name);
    setParamOptions(filteredNames);

    const CMinMax = (ele) => {
      const FminMax = filteredArray.filter((i) => i.name === ele);
      setCurrMinMax((prevMinMax) => [...prevMinMax, ...FminMax]);
    };

    selectedParameterName.forEach((e) => CMinMax(e));
  }, [selectedEqName]);

  const onSubmitHandler = () => {
    fetch("/fetch_cmdata", {
      method: "POST",
      body: JSON.stringify({
        EquipmentIds: selectedEqName.map((x) => x.id),
        ParameterNames: selectedParameterName.map((x) => x),
      }),
      headers: {
        "Content-Type": "application/json",
        Accept: "application/json",
      },
    })
      .then((res) => res.json())
      .then((data) => {
        setParamData(data);
        setShowGraph(true);
      })
      .catch((error) => {
        setSnackBarMessage({
          severity: "error",
          message: "Some Error Occurred. " + error,
          showSnackBar: true,
        });
      });
  };

  const changeShip = (e) => {
    const filteredEqData = userSelectionData.filter(
      (x) => x.shipName === e.target.value
    ).map((x) => {
      const id = uniqueEqIds.find((y) => y.name === x.equipmentName);
      return id;
    });

    setEqDataOption(filteredEqData);
    setShipName(e.target.value);
  };

  const [SnackBarMessage, setSnackBarMessage] = useState({
    severity: "error",
    message: "This is awesome",
    showSnackBar: false,
  });

  const onHandleSnackClose = () => {
    setSnackBarMessage({
      severity: "error",
      message: "Please Add Systems",
      showSnackBar: false,
    });
  };

  return (
    <MuiPickersUtilsProvider utils={MomentUtils}>
      <Navigation />
      <div className="body">
        <div className="mprofile">
          <div style={{ width: "300px" }}>
            <InputLabel className={classes.inputLabel}>Ship Name</InputLabel>
            <CustomSelect
              fields={customSelectData["shipName"]}
              onChange={changeShip}
              value={selectedShipName}
            />
          </div>
          <div style={{ width: "300px" }}>
            <InputLabel className={classes.inputLabel}>
              Equipment Name
            </InputLabel>
            <Autocomplete
              classes={classes}
              multiple
              id="tags-standard"
              options={eqDataOption}
              getOptionLabel={(option) => option.name}
              value={selectedEqName}
              onChange={(e, value) => setEquipmentName(value)}
              renderInput={(params) => (
                <TextField
                  {...params}
                  InputProps={{ ...params.InputProps, disableUnderline: true }}
                  variant="standard"
                />
              )}
            />
          </div>
          <div style={{ width: "300px" }}>
            <InputLabel className={classes.inputLabel}>
              Select Parameter
            </InputLabel>
            <Autocomplete
              classes={classes}
              multiple
              id="tags-standard"
              options={paramOptions}
              value={selectedParameterName}
              onChange={(e, value) => setParameterName(value)}
              renderInput={(params) => (
                <TextField
                  {...params}
                  InputProps={{ ...params.InputProps, disableUnderline: true }}
                  variant="standard"
                />
              )}
            />
          </div>
          <Button
            variant="contained"
            color="primary"
            onClick={onSubmitHandler}
          >
            Submit
          </Button>
        </div>

        {showGraph && (
          <div className="midSection">
            {paramData.map((param) => {
              if (param.data.length === 0) {
                return null;
              }

              const crossingThreshold =
                (param.data[param.data.length - 1]?.value ?? 0) <
                  param.minThreshold ||
                (param.data[param.data.length - 1]?.value ?? 0) >
                  param.maxThreshold;

              const matchingMinMax = currMinMax?.find(
                (item) => item.name === param.parameterName
              );

              const minThreshold = parseInt(matchingMinMax?.min_value);
              const maxThreshold = parseInt(matchingMinMax?.max_value);

              return (
                <div className="rchart" key={param.parameterName}>
                  <div className="content">
                    <div>
                      {crossingThreshold}
                      {param.equipmentName} {param.componentName}
                      {param.parameterName}
                    </div>
                    <LineChart width={550} height={300} data={param.data}>
                      <XAxis
                        dataKey="date"
                        tick={false}
                        label={{
                          value: "Date",
                          position: "insideBottom",
                          dy: 10,
                        }}
                        height={45}
                      />
                      <YAxis
                        domain={[0, 100]}
                        label={{
                          value: `${param.parameterName} Data`,
                          angle: -90,
                          position: "center",
                          paddingRight: "20px",
                          dy: -10,
                        }}
                        width={80}
                      />
                      <CartesianGrid horizontal={false} vertical={false} />
                      <Line
                        type="monotone"
                        dataKey="value"
                        stroke={crossingThreshold ? "red" : "green"}
                      />
                      <ReferenceLine
                        y={minThreshold}
                        stroke="gray"
                        strokeDasharray="6 6"
                      />
                      <ReferenceLine
                        y={maxThreshold}
                        stroke="gray"
                        strokeDasharray="6 6"
                      />
                      <Tooltip />
                    </LineChart>
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
      {SnackBarMessage.showSnackBar && (
        <CustomizedSnackbars
          message={SnackBarMessage}
          onHandleClose={onHandleSnackClose}
        />
      )}
    </MuiPickersUtilsProvider>
  );
};

export default CDashboard;
